import { ChatMain } from "@/registry/blocks/chat-04/components/chat/chat-main";
import { AppLayout } from "@/registry/blocks/chat-04/components/layout/app-layout";

export default function Page() {
	return (
		<AppLayout sidebarChildren={null}>
			<ChatMain />
		</AppLayout>
	);
}

import { agentExecute, agentRoute } from "@/registry/ai/agent-route-respond";
import { agents } from "@/registry/ai/agents/agents-registry";

export async function POST(req: Request) {
	const { messages, mentions } = await req.json();
	const agentId = agentRoute(mentions);
	const agent = agents[agentId];
	return agentExecute({ agentId, agent, messages });
}

import { HomeIcon, SettingsIcon } from "lucide-react";

export const config: {
	mainRoutes: {
		path: string;
		matchExpression: RegExp;
		label: string;
		icon: React.ComponentType;
	}[];
} = {
	mainRoutes: [
		{
			path: "/",
			label: "Home",
			matchExpression: /^\/$/,
			icon: HomeIcon,
		},
		{
			path: "/settings",
			label: "Settings",
			matchExpression: /^\/settings$/,
			icon: SettingsIcon,
		},
	],
};

import { SidebarInset, SidebarProvider } from "@/components/ui/sidebar";
import { AppSidebar } from "@/registry/blocks/chat-04/components/layout/app-sidebar";

export function AppLayout({
	children,
	sidebarChildren,
}: {
	children: React.ReactNode;
	sidebarChildren: React.ReactNode;
}) {
	return (
		<SidebarProvider defaultOpen={false}>
			<AppSidebar>{sidebarChildren}</AppSidebar>
			<div className="h-svh relative flex w-full flex-1 flex-col md:peer-data-[variant=inset]:p-2 md:peer-data-[state=collapsed]:peer-data-[variant=inset]:pl-2 md:peer-data-[variant=inset]:pl-0">
				<SidebarInset className="overflow-y-auto rounded-xl shadow">
					{children}
