	className,
	connectionStatus,
	...props
}: ComponentProps<"div"> & {
	connectionStatus: "connected" | "connecting" | "disconnected";
}) {
	return (
		<div
			className={cn(
				"h-2 w-2 rounded-full transition-colors",
