import { cn } from "@/lib/utils";
import React from "react";
import { NodeResizer } from "@xyflow/react";
import { BaseNode, type BaseNodeProps } from "@/components/ui/flow/base-node";

export const BaseResizableNode = React.forwardRef<
	HTMLDivElement,
	BaseNodeProps
>(
	(
		{ className, selected, executionStatus, style, children, ...props },
		ref,
	) => (
		<BaseNode
			ref={ref}
			style={{
				...style,
				minHeight: 200,
				minWidth: 250,
				maxHeight: 800,
				maxWidth: 800,
			}}
			className={cn(className, "h-full")}
			{...props}
		>
			<NodeResizer
				/* minHeight={200}
				minWidth={250}
				maxHeight={800}
				maxWidth={800} */
				isVisible={selected}
			/>
			{children}
		</BaseNode>
	),
);
BaseResizableNode.displayName = "BaseNodeResizable";
