export function ChatContent({
	className,
	...props
}: ComponentPropsWithoutRef<"div">) {
	const { messages, sendMessage, status, stop } = useChat({
		transport: new DefaultChatTransport({
			api: "/api/ai/chat",
		}),
		messages: INITIAL_MESSAGES,
	});

	const isLoading = status === "streaming" || status === "submitted";

	// Use the new hook with custom onSubmit
	const { value, onChange, handleSubmit } = useChatInput({
		onSubmit: (parsedValue) => {
			// Custom logic: log, send, access type-safe fields
			console.log("Submitted parsed:", parsedValue);

			sendMessage({
				role: "user",
				parts: [{ type: "text", text: parsedValue.content }],
			});
		},
	});

	return (
		<div className="flex-1 flex flex-col overflow-y-auto" {...props}>
			<ChatMessageArea>
				<ChatMessageAreaContent className="pt-6">
					{messages.map((message) => {
						const userName =
							message.role === "user" ? "You" : "Assistant";
						return (
							<ChatMessage key={message.id}>
								<ChatMessageActions>
									<ChatMessageAction label="Copy">
										<Copy className="size-4" />
									</ChatMessageAction>
									<ChatMessageAction label="Like">
										<ThumbsUp className="size-4" />
									</ChatMessageAction>
								</ChatMessageActions>
								<ChatMessageAvatar>
									<ChatMessageAvatarImage
										src={message.metadata?.member.image}
									/>
									<ChatMessageAvatarFallback>
										{message.metadata?.member.name
											.charAt(0)
											.toUpperCase()}
									</ChatMessageAvatarFallback>
								</ChatMessageAvatar>

								<ChatMessageContainer>
									<ChatMessageHeader>
										<ChatMessageAuthor>
											{userName}
										</ChatMessageAuthor>
										<ChatMessageTimestamp
											createdAt={new Date()}
										/>
									</ChatMessageHeader>

									<ChatMessageContent>
										{message.parts.map((part, index) => {
											if (part.type === "text") {
												return (
													<ChatMessageMarkdown
														key={`${message.id}-text-${index}`}
														content={part.text}
													/>
												);
											}
											if (part.type.startsWith("tool-")) {
												if (
													!("toolCallId" in part) ||
													!("state" in part)
												) {
													return null;
												}

												const toolPart = part as {
													type: string;
													toolCallId: string;
													state:
														| "input-streaming"
														| "input-available"
														| "output-available"
														| "output-error";
													input?: unknown;
													output?: unknown;
													errorText?: string;
												};

												const hasInput =
													toolPart.input != null &&
													toolPart.input !==
														undefined;
												const hasOutput =
													toolPart.output != null &&
													toolPart.output !==
														undefined;

												const toolName =
													toolPart.type.slice(5);
												return (
													<ToolInvocation
														key={
															toolPart.toolCallId
														}
														className="w-full"
													>
														<ToolInvocationHeader>
															<ToolInvocationName
																name={toolName}
																type={
																	toolPart.state
																}
																isError={
																	toolPart.state ===
																	"output-error"
																}
															/>
														</ToolInvocationHeader>
														{(hasInput ||
															hasOutput ||
															toolPart.errorText) && (
															<ToolInvocationContentCollapsible>
																{hasInput && (
																	<ToolInvocationRawData
																		data={
																			toolPart.input
																		}
																		title="Arguments"
																	/>
																)}
																{toolPart.errorText && (
																	<ToolInvocationRawData
																		data={{
																			error: toolPart.errorText,
																		}}
																		title="Error"
																	/>
																)}
																{hasOutput && (
																	<ToolInvocationRawData
																		data={
																			toolPart.output
																		}
																		title="Result"
																	/>
																)}
															</ToolInvocationContentCollapsible>
														)}
													</ToolInvocation>
												);
											}
											return null;
										})}
									</ChatMessageContent>

									{message.metadata?.threadData && (
										<ChatMessageThread>
											<ChatMessageAvatar>
												<ChatMessageAvatarImage
													src={
														message.metadata
															.threadData.member
															.image
													}
												/>
												<ChatMessageAvatarFallback>
													{message.metadata.threadData.member.name
														.charAt(0)
														.toUpperCase()}
												</ChatMessageAvatarFallback>
											</ChatMessageAvatar>
											<ChatMessageThreadReplyCount>
												{
													message.metadata.threadData
														.messageCount
												}{" "}
												replies
											</ChatMessageThreadReplyCount>
											<ChatMessageThreadTimestamp
												date={
													message.metadata.threadData
														.lastReply
												}
											/>
											<ChatMessageThreadAction />
										</ChatMessageThread>
									)}
								</ChatMessageContainer>
							</ChatMessage>
						);
					})}
				</ChatMessageAreaContent>
				<ChatMessageAreaScrollButton alignment="center" />
			</ChatMessageArea>
			<div className="px-2 py-4 max-w-2xl mx-auto w-full">
				<ChatInput
					onSubmit={handleSubmit}
					isStreaming={isLoading}
					onStop={stop}
				>
					<ChatInputEditor
						value={value}
						onChange={onChange}
						placeholder="Type a message..."
					/>
					<ChatInputGroupAddon align="block-end">
						<ChatInputSubmitButton className="ml-auto" />
					</ChatInputGroupAddon>
				</ChatInput>
			</div>
		</div>
	);
}
