"use client";

import { Button } from "@/components/ui/button";
import { Chat } from "@/components/chat";
import { X } from "lucide-react";
import { useState } from "react";

export default function Page() {
	const [open, setOpen] = useState(true);

	return (
		<div className="h-screen flex items-center justify-center">
			<Button size="lg" onClick={() => setOpen(true)}>
				Open Chat
			</Button>
			<div className="fixed bottom-4 right-4 z-50 flex flex-col items-end gap-4">
				{open && (
					<div className="w-[400px] h-[600px] bg-background border rounded-lg shadow-lg flex flex-col overflow-hidden">
						<div className="flex items-center justify-between p-4 border-b">
							<h2 className="font-semibold">Chat</h2>
							<Button
								variant="ghost"
								size="icon"
								onClick={() => setOpen(false)}
							>
								<span className="sr-only">Close chat</span>
								<X />
							</Button>
						</div>
						<Chat />
					</div>
				)}
			</div>
		</div>
	);
}

import { openai } from "@ai-sdk/openai";
import { streamText } from "ai";

export async function POST(req: Request) {
	const { messages } = await req.json();

	const result = streamText({
		model: openai("gpt-4o-mini"),
		system: "You are a helpful assistant",
		messages,
	});

	return result.toDataStreamResponse();
}

"use client";
import {
	ChatInput,
	ChatInputSubmit,
	ChatInputTextArea,
} from "@/components/ui/chat-input";
import {
	ChatMessage,
	ChatMessageAvatar,
	ChatMessageContent,
} from "@/components/ui/chat-message";
import { ChatMessageArea } from "@/components/ui/chat-message-area";
import { useChat } from "ai/react";

export function Chat() {
	const { messages, input, handleInputChange, handleSubmit, isLoading, stop } =
		useChat({
			api: "/api/ai/chat",
			initialMessages: [
				{
					id: "1",
					content:
						"Hello! Welcome to our customer support. How can I assist you today?",
					role: "assistant",
				},
				{
					id: "2",
					content:
						"Hi, I received my order yesterday but the size of the shirt is too small. I'd like to exchange it for a larger size.",
					role: "user",
				},
				{
					id: "3",
					content:
						"I'm sorry to hear that the size didn't work out. I'll be happy to help you with the exchange. Could you please provide your order number? You can find it in your confirmation email.",
					role: "assistant",
				},
			],
			onFinish: (message) => {
				//console.log("onFinish", message, completion);
			},
		});

	const handleSubmitMessage = () => {
		if (isLoading) {
			return;
		}
		handleSubmit();
	};

	return (
		<div className="flex flex-col h-full overflow-y-auto">
			<ChatMessageArea className="px-4 py-4 space-y-4">
				{messages.map((message) => {
					if (message.role !== "user") {
						return (
							<ChatMessage
								key={message.id}
								id={message.id}
								variant="bubble"
								type="incoming"
							>
								<ChatMessageAvatar imageSrc="/avatar-1.png" />
								<ChatMessageContent content={message.content} />
							</ChatMessage>
						);
					}
					return (
						<ChatMessage
							key={message.id}
							id={message.id}
							variant="bubble"
							type="outgoing"
						>
							<ChatMessageContent content={message.content} />
							<ChatMessageAvatar imageSrc="/avatar-1.png" />
						</ChatMessage>
					);
				})}
			</ChatMessageArea>
			<div className="px-2 py-2 border-t">
				<ChatInput
					value={input}
					onChange={handleInputChange}
					onSubmit={handleSubmitMessage}
					loading={isLoading}
					onStop={stop}
				>
					<ChatInputTextArea placeholder="Type a message..." />
					<ChatInputSubmit />
				</ChatInput>
			</div>
		</div>
	);
}
